import React, { Component } from 'react';
import './css/Submissions.css';

import  { FirebaseContext } from './Firebase';

export default class Submissions extends Component {
	constructor(props) {
		super(props);
		this.state = {
			name: '',
			title: '',
			email: '',
			medium: '',
			year: '',
			dateCompleted: '',
			school: '',
			websiteLink: '',
			artistStatement: '',
		};

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {

		const target = event.target;
		const value = target.value;
		const name = target.name;

		this.setState({
			[name]: value
		});
	}

	handleSubmit(event) {
		event.preventDefault();

		FirebaseContext.Consumer
			{ firebase => {
				const db = firestore();
				db.settings({
					timestampsInSnapshots: true
				});
				const userRef = db.collection("submissions").add({
					fullname: this.state.fullname,
					email: this.state.email
				});  
			}
		}

		this.setState({
			name: '',
			title: '',
			email: '',
			medium: '',
			year: '',
			dateCompleted: '',
			school: '',
			websiteLink: '',
			artistStatement: '',
		});
	}

	render() {
		return (
			<div>
				<form onSubmit={this.handleSubmit}>
					<input type="text" name="name" placeholder="name" spellCheck="false" value={this.state.name} onChange={this.handleChange} />
					<input type="text" name="title" placeholder="title" spellCheck="false" value={this.state.title} onChange={this.handleChange} />
					<input type="text" name="email" placeholder="email" spellCheck="false" value={this.state.email} onChange={this.handleChange} />
					<input type="text" name="medium" placeholder="medium" spellCheck="false" value={this.state.medium} onChange={this.handleChange} />
					<input type="text" name="year" placeholder="year" spellCheck="false" value={this.state.year} onChange={this.handleChange} />
					<input type="text" name="dateCompleted" placeholder="date completed (mm/dd/yy)" spellCheck="false" value={this.state.dateCompleted} onChange={this.handleChange} />
					<input type="text" name="school" placeholder="school" spellCheck="false" value={this.state.school} onChange={this.handleChange} />
					<input type="text" name="websiteLink" placeholder="website link" spellCheck="false" value={this.state.websiteLink} onChange={this.handleChange} />
					<textarea name="artistStatement" value={this.state.artistStatement} onChange={this.handleChange} placeholder="artist statement" spellCheck="false" />
					<input className="upload" type="file" accept=".jpg, .png, .pdf" />
					<input className ="submit" type="submit" value="submit" />
					<p className="disclaimer">By checking this box you are agreeing to Roski Magâ€™s terms and conditions. By submitting your work, you are agreeing to allow Roski Mag to potentially publish it on its online platform and printed magazine.</p>
				</form>

				<script></script>
			</div>
		)
	}
}
