import React, { Component } from 'react';
import './css/Submissions.css'

export default class Submissions extends Component {
	constructor(props) {
		super(props);
		this.state = {
			artistStatement: 'artist statement'
		};

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {
		this.setState({artistStatement: event.target.value});
	}

	handleSubmit(event) {
		alert('An essay was submitted: ' + this.state.value);
		event.preventDefault();
	}

	render() {
	return (
		<div>
			<form onSubmit={this.handleSubmit}>
				<input type="text" name="name" placeholder="name" spellCheck="false" />
				<input type="text" name="title" placeholder="title" spellCheck="false" />
				<input type="text" name="email" placeholder="email" spellCheck="false" />
				<input type="text" name="medium" placeholder="medium" spellCheck="false" />
				<input type="text" name="year" placeholder="year" spellCheck="false" />
				<input type="text" name="date completed" placeholder="date completed" spellCheck="false" />
				<input type="text" name="school" placeholder="school" spellCheck="false" />
				<input type="text" name="website link" placeholder="website link" spellCheck="false" />
				<textarea value={this.state.artistStatement} onChange={this.handleChange} />
				<input className="upload" type="file" accept=".jpg, .png, .pdf" />
				<input className ="submit" type="submit" value="submit" />
				<p className="disclaimer">By checking this box you are agreeing to Roski Magâ€™s terms and conditions. By submitting your work, you are agreeing to allow Roski Mag to potentially publish it on its online platform and printed magazine.</p>
			</form>
		</div>
	)
	}
}
